query PostViewerEdgeContentQuery(
  $postId: ID!

) {
  post(id: $postId) {
    ... on Post {
      id
      title
      viewerEdge {

        fullContent {
          bodyModel {
               paragraphs {
                    text
                    type
                    }
                }
            }
        } 
    }
  }
}
